import "./ExpenseForm.css";
import { useState } from "react";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [DisplayedExpenses, setDisplayedExpenses] = useState(false);

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredAmount("");
    setEnteredDate("");
    setEnteredTitle("");
    setDisplayedExpenses(false);
  };

  const cancelHandler = () => {
    setDisplayedExpenses(false);
    setEnteredAmount("");
    setEnteredDate("");
    setEnteredTitle("");
  }

  const addExpenseHandler = () => {
    DisplayedExpenses === true ? setDisplayedExpenses(false) : setDisplayedExpenses(true);
  }

  if (DisplayedExpenses === false) {
    return (
      <div className="new-expense__actions">
        <button type="submit" onClick={addExpenseHandler}> Add Expense </button>
      </div>
    );
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit" onClick={cancelHandler}> Add Expense </button>
        <button type="submit" onClick={cancelHandler}> Cancel </button>
      </div>
    </form>
  );
};

export default ExpenseForm;
