import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./DisplayExpenses.css";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import React, { useState } from "react";

const DisplayExpenses = (props) => {
  
  const [year, setYear] = useState("2020");

  const selectedYearHandle = (expectedYear) => {
    setYear(expectedYear);
    console.log(year);
  };

  const filterYear = (arr, year) => {
    return arr.filter((elem) => elem.date.getFullYear().toS === year);
  };
  
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onSelectYear={selectedYearHandle} initValue={year} />
        {filterYear(props.items, year).map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default DisplayExpenses;
